import { StyleSheet, View, Text, Dimensions, Modal } from 'react-native'
import React from 'react'
import AddSVG from '../../assets/AddSVG.js'

let showingAboutUs = false;

export function showAboutUs(show) {
    showingAboutUs = show;
}

const vw = Dimensions.get('window').width;

const style = StyleSheet.create({
    container: {
        backgroundColor: "#000000aa",
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },

    wrapper: {
        backgroundColor: "#ffffff",
        padding: vw * .04,
        borderRadius: vw * .04,
        width: vw * .9,
        height: vw,
    },

    content: {
        flex: 1,
        textWrap: "wrap",
    },

    header: {
        flexDirection: "row",
    },

    heading: {
        fontSize: vw * .05,
        fontFamily: "BalooTamma2-Medium",
    },

    icon: {
        width: vw * .05,
        height: vw * .05,
        marginLeft: "auto",
    },

    text: {
        fontSize: vw * .04,
        fontfamily: "Comfortaa-Regular",
    },
});

const EditProfile = () => {
    return (
        <Modal
            transparent={true}
            visible={showingAboutUs}
        >
            <View style={style.container}>
                <View style={style.wrapper}>
                    <View style={style.header}>
                        <Text style={style.heading}>About Us</Text>
                        <AddSVG
                            fill="#000"
                            style={[style.icon,
                            {
                                transform: [{ rotate: '45deg' }]
                            }]}
                        />
                    </View>
                    <View style={style.content}>
                        <Text style={style.text}>This is filler text. Lorem Ispum I don't know the rest of the lorem ispum. someone is gonna write the about page sooner or later. If there are any grammar errors, so but I didn't really bother</Text>
                    </View>
                </View>

            </View>
        </Modal>
    )
}

export default EditProfile;
