import { Text, View, StyleSheet, Dimensions } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import ArrowSVG from '../assets/ArrowSVG.js';
import * as d3 from 'd3';

export default (props) => {
    const vw = Dimensions.get("window").width;

    const systolicScale = d3.scaleLinear()
        .domain([90, 140])
        .range([(vw * .4) - (vw * .06), 0])
        .clamp(true)

    const diastolicScale = d3.scaleLinear()
        .domain([0, 1])
        .range([(vw * .4) - (vw * .06), 0])
        .clamp(true)

    const offsetSystolic = systolicScale(props.data.systolic);

    let offset = offsetSystolic;

    if (props.data.diastolic < 60) {
        offset = diastolicScale(0);
    } else if (props.data.diastolic > 90) {
        offset = diastolicScale(1);
    }

    const style = StyleSheet.create({
        container: {
            flex: 1,
            padding: vw * .01,
        },

        headingContainer: {
            width: vw * .6,
            flexDirection: "row",
            marginLeft: "auto",
            marginRight: "auto",
            marginBottom: vw * .04,
        },

        heading: {
            fontSize: vw * .04,
            color: "#000",
            fontFamily: "BalooTamma2-SemiBold",
            marginRight: "auto",
        },

        leftHeading: {
            fontSize: vw * .04,
            color: "#000",
            fontFamily: "BalooTamma2-SemiBold",

        },

        text: {
            color: "#000",
            fontFamily: "Comfortaa-Regular",
            fontSize: vw * .03,
            alignText: "center",
            marginLeft: "auto",
            marginRight: "auto",
        },

        scaleContainer: {
            flex: 1,
            justifyContent: "center"
        },

        scaleSpacer: {
            width: vw * .05,
        },

        arrowContainer: {
            flexDirection: "column",
            // position: "absolute",
            backgroundColor: "red",
            width: vw * .2,
        },

        scale: {
            marginLeft: "auto",
            marginRight: "auto",
            width: vw * .6,
            height: vw * .04,
            borderRadius: vw * .04,
            justifyContent: "center",
            alignContent: "center",
        },

        scaleSecond: {
            marginTop: vw * .08,
        },

        graph: {
            flexDirection: "column",
        },
    });

    return (
        <View style={style.container}>
            <View style={style.headingContainer}>
                <Text style={style.heading}>Jan 1</Text>
                <Text style={style.leftHeading}>Blood Pressure</Text>
            </View>
            <View style={style.scaleContainer}>
                <View style={style.graph}>
                    <View style={style.arrowContainer}>
                        <Text style={style.text}>systolic: {props.data.systolic}</Text>
                        <ArrowSVG
                            width={vw * .04} />
                    </View>
                    <LinearGradient
                        style={style.scale}
                        colors={['#E24D50', '#38B346', "#3399FF"]}
                        start={{ x: 0, y: 0 }}
                        end={{ x: 1, y: 0 }}
                        locations={[0, 0.5, 1]}
                    />
                </View>
                <View style={style.graph}>
                    <View style={style.arrowContainer}>
                        <Text style={style.text}>diastolic {props.data.diastolic}</Text>
                        <ArrowSVG

                            width={vw * .04} />
                    </View>
                    <LinearGradient
                        style={[style.scale, style.scaleSecond]}
                        colors={['#E24D50', '#38B346', "#3399FF"]}
                        start={{ x: 0, y: 0 }}
                        end={{ x: 1, y: 0 }}
                        locations={[0, 0.5, 1]}
                    />
                </View>
            </View>
        </View>
    );
}
