import { Dimensions } from 'react-native';
import { general, bloodSugarGraph } from './../style/style';
import { LineChart, BarChart, PieChart, ProgressChart, ContributionGraph, StackedBarChart } from "react-native-chart-kit";

export default function BloodSugarGraph() {
  const sugarData = [95, 99, 112];
  let yHideRatio = 0.8;
  let defaultMax = 250;
  let opacity = 0.5;
  const vw = Dimensions.get("window").width;
  return (
    <LineChart
      style={
        {
          shadowColor: "#000000",
          shadowOffset: { width: vw * .1, height: vw * .1 },
          shadowOpacity: .2,
          shadowRadius: vw(2),
          marginVertical: 8,
          borderRadius: 10,
          width: vw(80),
        }
      }
      data={{
        labels: ["sun", "mon", "tue"],
        datasets: [
          {
            data: [
              Math.random() * 100,
              Math.random() * 100,
              Math.random() * 100
            ]
          }
        ]
      }}
      width={Dimensions.get("window").width * .8} // from react-native
      height={Dimensions.get("window").width * .5}
      // yAxisLabel="$"
      // yAxisSuffix="k"
      yAxisInterval={1} // optional, defaults to 1
      chartConfig={{
        backgroundColor: `rgba(255, 255, 255, ${opacity}})`,
        backgroundGradientFrom: `rgba(255, 255, 255, ${opacity})`,
        backgroundGradientTo: `rgba(255, 255, 255, ${opacity})`,
        decimalPlaces: 2, // optional, defaults to 2dp
        color: (opacity = .9) => `rgba(0, 0, 0, ${opacity})`,
        labelColor: (opacity = .9) => `rgba(0, 0, 0, ${opacity})`,
        style: {
          borderRadius: 10,
        },
        propsForDots: {
          r: "6",
          strokeWidth: "2",
          stroke: "#fff"
        }
      }}
    />
  );
}
