import { StyleSheet, View, Text, Dimensions, Modal } from 'react-native'
import React from 'react'
import AddSVG from '../../assets/AddSVG.js'
import { TouchableOpacity } from 'react-native-web';

let showingProfile = false;

export function showProfile(show) {
  showingProfile = show;
}

const vw = Dimensions.get('window').width;

const style = StyleSheet.create({
  container: {
    backgroundColor: "#000000aa",
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },

  wrapper: {
    backgroundColor: "#ffffff",
    padding: vw * .04,
    borderRadius: vw * .04,
    width: vw * .9,
    height: vw,
  },

  header: {
    flexDirection: "column",
    width: "auto",
    height: vw * .06,
    justifyContent: "center",
    alignItems: "center",
  },

  heading: {
    fontSize: vw * .04,
  },

  icon: {
    marginLeft: "auto",
    width: vw * .04,
    height: vw * .04,
  }
});

const Profile = () => {
  return (
    <Modal
      transparent={true}
      visible={showingProfile}
    >
      <View style={style.container}>
        <View style={style.wrapper}>
          <Text style={style.heading}>Profile</Text>
          <TouchableOpacity>
            <AddSVG
              fill="#000"
              style={[style.icon,
              {
                transform: [{ rotate: '45deg' }]
              }]}
            />
          </TouchableOpacity>
          <View style={style.header}>
          </View>
          <View style={style.content}>
          </View>
        </View>

      </View>
    </Modal>
  )
}

export default Profile;
