import { Dimensions, View, Text } from 'react-native';
import { bloodSugarGraph } from '../style/style';
import Svg, { Path } from 'react-native-svg';
import { parseISO } from 'date-fns'
import * as d3 from 'd3';
import { Data } from './Data.js';

export default () => {
  const sugarData = [95, 99, 112];
  let yHideRatio = 0.8;
  let defaultMax = 250;

  const vw = Dimensions.get("window").width;

  const width = vw * .65;
  const height = width;
  const margin = {
    top: vw * .1,
    bottom: vw * .1,
    left: vw * .06,
    right: vw * .1,
  }

  const makeLine = (d) => {
    for (let i = 0; i < d.length; i++) {
      d[i][0] = parseISO(Data[i][0]);
    }
   
    const xAxis = d3.scaleLinear()
      .domain([new Date(d[0][0]), new Date(d[d.length - 1][0])])
      .range([margin.left, width - margin.right]);

    const yAxis = d3.scaleLinear()
      .domain(d3.extent(d.map((i) => i[1])))
      .range([height - margin.bottom, margin.top]);

    const line = d3.line()
      .x(i => xAxis(i[0]))
      .y(i => yAxis(i[1]))
    let graph = line(Data);
    let html = [];

    yAxis.ticks().reverse().forEach((i) => {
      html.push(<Text key={i} style={bloodSugarGraph.yLabel}>{i}</Text>)
    });

    return [graph, html];
  }

  console.log(D)

  return (
    <View style={bloodSugarGraph.container}>
      <View style={bloodSugarGraph.content}>
        <View style={bloodSugarGraph.unitContainer}>
          <Text style={[bloodSugarGraph.unit,
          {
            transform: [{ rotate: '270deg' }]
          }]}>
            ppm</Text>
        </View>
        <View style={bloodSugarGraph.labelContainer}>
          {makeLine(Data)[1]}
        </View>
        <Svg
          width={width}
          height={height}
          xmlns="http://www.w3.org/2000/svg"
        >
          <Path
            fill="none"
            // stroke="#ff7e40"
            stroke="#ff9933"
            // stroke="#66cc99"
            strokeWidth={vw * .015}
            strokeLinecap="round"
            strokeLinejoin="round"
            d={makeLine(Data)[0]}
          />
        </Svg>
      </View>
      <View style={bloodSugarGraph.xLabelContainer}>
        <Text style={bloodSugarGraph.xLabel}>Acetone Levels</Text>
      </View>
    </View>

  );
}
